        public Pokemon(GameType version, short species, byte level)
        {
            _version = version;
            _species = species;
            _level = level;
        }

        public GameType Version
        {
            get
            {
                return _version;
            }
        }

        public short Species
        {
            get
            {
                if (this.Version == GameType.RBY)
                {
                    if (_species > _gen1species2dex.Length) return 0;
                    else return _gen1species2dex[_species];
                }
                else return _species;
            }
        }
        public byte Level
        {
            get
            {
                switch (this.Version)
                {
                    case GameType.CRYSTAL:
                        return (this.Species == 0xFD) ? (byte)0 : this._level;
                    case GameType.EMERALD:
                        return (this.Species == 0x19C) ? (byte)0 : this._level;
                    default:
                        return _level;
                }

            }
        }

